CREATE TABLE `evento_avaliacoes` (
  `id` int(11) NOT NULL,
  `evento_id` int(11) NOT NULL,
  `usuario_id` int(11) NOT NULL,
  `tipo_usuario` enum('cliente','admin','funcionario') NOT NULL,
  `rating` int(11) NOT NULL CHECK (`rating` >= 1 and `rating` <= 5),
  `comentario` text DEFAULT NULL,
  `aprovado` tinyint(1) DEFAULT 0,
  `data_evento` date DEFAULT NULL,
  `anonimo` tinyint(1) DEFAULT 0,
  `created_at` timestamp NULL DEFAULT current_timestamp(),
  `updated_at` timestamp NULL DEFAULT current_timestamp() ON UPDATE current_timestamp()
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

--
-- Acionadores `evento_avaliacoes`
--
DELIMITER $$
CREATE TRIGGER `update_rating_after_avaliacao` AFTER INSERT ON `evento_avaliacoes` FOR EACH ROW BEGIN
        UPDATE eventos 
        SET 
            rating_medio = (
                SELECT AVG(rating) 
                FROM evento_avaliacoes 
                WHERE evento_id = NEW.evento_id AND aprovado = 1
            ),
            total_avaliacoes = (
                SELECT COUNT(*) 
                FROM evento_avaliacoes 
                WHERE evento_id = NEW.evento_id AND aprovado = 1
            )
        WHERE id = NEW.evento_id;
    END
$$
DELIMITER ;
DELIMITER $$
CREATE TRIGGER `update_rating_after_delete_avaliacao` AFTER DELETE ON `evento_avaliacoes` FOR EACH ROW BEGIN
        UPDATE eventos 
        SET 
            rating_medio = COALESCE((
                SELECT AVG(rating) 
                FROM evento_avaliacoes 
                WHERE evento_id = OLD.evento_id AND aprovado = 1
            ), 0),
            total_avaliacoes = (
                SELECT COUNT(*) 
                FROM evento_avaliacoes 
                WHERE evento_id = OLD.evento_id AND aprovado = 1
            )
        WHERE id = OLD.evento_id;
    END
$$
DELIMITER ;
DELIMITER $$
CREATE TRIGGER `update_rating_after_update_avaliacao` AFTER UPDATE ON `evento_avaliacoes` FOR EACH ROW BEGIN
        UPDATE eventos 
        SET 
            rating_medio = (
                SELECT AVG(rating) 
                FROM evento_avaliacoes 
                WHERE evento_id = NEW.evento_id AND aprovado = 1
            ),
            total_avaliacoes = (
                SELECT COUNT(*) 
                FROM evento_avaliacoes 
                WHERE evento_id = NEW.evento_id AND aprovado = 1
            )
        WHERE id = NEW.evento_id;
    END
$$
DELIMITER ;

