--
-- Acionadores `ingressos_pedidos`
--
DELIMITER $$
CREATE TRIGGER `ingressos_pedidos_after_update` AFTER UPDATE ON `ingressos_pedidos` FOR EACH ROW BEGIN
        IF NEW.validado = 1 AND OLD.validado = 0 THEN
            IF (
                SELECT COUNT(*) 
                FROM ingressos_pedidos 
                WHERE order_id = NEW.order_id AND validado = 1
            ) = (
                SELECT COUNT(*) 
                FROM ingressos_pedidos 
                WHERE order_id = NEW.order_id
            ) THEN
                UPDATE pedidos 
                SET ingresso_validado = 1 
                WHERE order_id = NEW.order_id;
            END IF;
        END IF;
    END
$$
DELIMITER ;

