DELIMITER $$
CREATE TRIGGER `pedidos_validacao_update` AFTER UPDATE ON `pedidos` FOR EACH ROW BEGIN
        IF NEW.ingresso_validado = 1 AND OLD.ingresso_validado = 0 THEN
            UPDATE ingressos_pedidos
               SET validado = 1,
                   validado_em = NOW()
             WHERE order_id = NEW.order_id
               AND validado = 0;
        END IF;
    END
$$
DELIMITER ;

